mapper.select:
SELECT STATECODE, TO_CHAR(CLASSDATE, 'MM"월"DD"일"') CLASSDATE
FROM ATTENDANCE
WHERE STNO = #{stNo}
AND SBJNO = #{sbjNo}
ORDER BY CLASSDATE

SELECT * 
FROM attendance
WHERE stno = #{stno}
AND sbjno = #{sbjno}
ORDER BY stno

		SELECT no, statecode, stno, sbjno, classdate
		FROM attendance
		WHERE classdate = #{date}
    	AND sbjno = #{sbjno}

	SELECT no, statecode, stno, sbjno, classdate
	FROM attendance
