mapper.select:
SELECT A.STATECODE, A.STNO, A.SBJNO, A.CLASSDATE 
FROM ATTENDANCE A, LECTUREAPPLY L
WHERE A.SBJNO = L.SBJNO
AND A.STNO = L.STNO
AND L.STNO = #{stNo}
AND L.SEASONCODE = #{seasonCode}
AND A.SBJNO = #{sbjNo}
ORDER BY CLASSDATE

SELECT STATECODE, TO_CHAR(CLASSDATE, 'MM"월"DD"일"') CLASSDATE
FROM ATTENDANCE
WHERE STNO = #{stNo}
AND SBJNO = #{sbjNo}
ORDER BY CLASSDATE

SELECT * 
FROM attendance
WHERE stno = #{stno}
AND sbjno = #{sbjno}
ORDER BY stno

		SELECT no, statecode, stno, sbjno, classdate
		FROM attendance
		WHERE classdate = #{date}
    	AND sbjno = #{sbjno}

	SELECT no, statecode, stno, sbjno, classdate
	FROM attendance
	WHERE classdate = #{date}
    AND sbjno = #{sbjno}
    AND stno = #{stno}

mapper.update:
UPDATE attendance
statecode = #{stateCode}, stno = #{stNo}, sbjno = #{sbjNo},
WHERE no = #{no}
