mapper.update:
UPDATE BANNER
SET SEQUENCE = #{sequence}
WHERE NO = #{no}

mapper.select:
select *
from (select rownum as rnum,
             b.*
      from banner b
      order by no desc)
where #{startList} < rnum and rnum <= #{endList}

	SELECT *
	FROM BANNER

	WHERE NO = (
					SELECT MAX(NO) 
					FROM BANNER 
					WHERE BANNERCODE = #{bannerCode}
			   )


    WHERE BANNERCODE = #{bannerCode} 
    AND SYSDATE BETWEEN STARTDATE AND ENDDATE
