mapper.select:
SELECT *
FROM grade
WHERE stno = #{stno}
	AND sbjno = #{sbjno}

mapper.update:
UPDATE grade
middle = #{middle}, finals = #{finals}, attd = #{attd}, task = #{task}, total = #{total},
WHERE no = #{no}

UPDATE grade
middle = #{middle}, finals = #{finals}, attd = #{attd}, task = #{task}, total = #{total},
WHERE stno = #{stNo} AND sbjno = #{sbjNo}
