mapper.select:
SELECT DISTINCT S.NO NO, S.NAME NAME, S.TARGET TARGET, S.POINT POINT, S.LIMIT LIMIT, S.TYPE TYPE, S.DEPTCODE DEPTCODE , S.SEASONCODE SEASONCODE,
P.NAME PRONAME,
L.ROOMCODE ROOMCODE, LISTAGG(L.TIMECODE, ' ') WITHIN GROUP (ORDER BY L.TIMECODE DESC) OVER (PARTITION BY S.NO) AS TIMECODE, LISTAGG(L.DAYCODE, '/') WITHIN GROUP (ORDER BY L.DAYCODE DESC) OVER (PARTITION BY S.NO) AS DAYCODE FROM SUBJECT S, LECTURESCHEDULE L, PROFESSOR P, STUDENT S2, LECTUREAPPLY L2 WHERE S.NO = L.SBJNO AND S.PFNO = P.NO AND S2.NO = L2.STNO AND L2.SBJNO = S.NO AND S.NO IN ( SELECT NO FROM SUBJECT) AND S.SEASONCODE = 106 AND STNO = #{stNo}

SELECT DISTINCT S.NO NO, S.NAME NAME, S.TARGET TARGET, S.POINT POINT, S.LIMIT LIMIT, S.TYPE TYPE, S.DEPTCODE DEPTCODE , S.SEASONCODE SEASONCODE,
P.NAME PRONAME,
L.ROOMCODE ROOMCODE, LISTAGG(L.TIMECODE, ' ') WITHIN GROUP (ORDER BY L.TIMECODE DESC) OVER (PARTITION BY S.NO) AS TIMECODE, LISTAGG(L.DAYCODE, '/') WITHIN GROUP (ORDER BY L.DAYCODE DESC) OVER (PARTITION BY S.NO) AS DAYCODE FROM SUBJECT S, LECTURESCHEDULE L, PROFESSOR P, STUDENT S2, LECTUREBASKET L2 WHERE S.NO = L.SBJNO AND S.PFNO = P.NO AND S2.NO = L2.STNO AND L2.SBJNO = S.NO AND S.NO IN ( SELECT NO FROM SUBJECT) AND S.SEASONCODE = 106 AND STNO = #{stNo}
