mapper.select:
SELECT N.NO, N.TITLE, TO_CHAR(N.ENROLLDATE, 'YYYY-MM-DD HH24:MI') ENROLLDATE
FROM LECTUREAPPLY L JOIN LECTURENOTICE N
ON(L.SBJNO = N.SBJNO)
JOIN SUBJECT J
ON(J.NO = N.SBJNO)
WHERE L.STNO = #{stNo}
AND L.SEASONCODE = #{seasonCode}
AND N.SBJNO = #{sbjNo}
ORDER BY N.NO DESC

SELECT N.NO, N.TITLE, N.CONTENT AS "CONTENT", TO_CHAR(N.ENROLLDATE, 'YYYY-MM-DD HH24:MI') ENROLLDATE
FROM LECTUREAPPLY L JOIN LECTURENOTICE N
ON(L.SBJNO = N.SBJNO)
JOIN SUBJECT J
ON(J.NO = N.SBJNO)
WHERE L.STNO = #{stNo}
AND L.SEASONCODE = #{seasonCode}
AND N.SBJNO = #{sbjNo}
AND N.NO = #{no}
