mapper.select:

		SELECT O.*, 
               OBJECTIONSTATE(O.SBJNO, O.STNO) AS OBJSTATE
		FROM OBJECTION O, SUBJECT S
		WHERE O.SBJNO = S.NO
        AND STNO = #{stNo}
        AND S.SEASONCODE = #{seasonCode}

mapper.update:

UPDATE OBJECTION
SET ANSWERDATE = SYSDATE,
    ANSWER = #{answer}
WHERE no = #{no}
