mapper.select:
SELECT u.* FROM (SELECT ROWNUM RN, LOGIN.* FROM (SELECT S2.NO, S2.NAME, S2.STATECODE, S2.SY, (SELECT NVL(ROUND(SUM((CASE WHEN G.TOTAL BETWEEN 95 AND 100 THEN 4.5 WHEN G.TOTAL >= 90 THEN 4.0 WHEN G.TOTAL >= 85 THEN 3.5 WHEN G.TOTAL >= 80 THEN 3.0 WHEN G.TOTAL >= 75 THEN 2.5 WHEN G.TOTAL >= 70 THEN 2.0 WHEN G.TOTAL >= 65 THEN 1.5 WHEN G.TOTAL BETWEEN 60 AND 64 THEN 1.0 ELSE 0 END ) * S.POINT) / SUM(S.POINT),2),0) AS TOTAL FROM GRADE G, SUBJECT S WHERE S.SEASONCODE = 105 AND S.NO = G.SBJNO AND STNO = S2.NO) AS AVGSCORE, S2.DEPTCODE, S2.ENTERDATE FROM STUDENT S2 WHERE NO IN (SELECT NO FROM STUDENT) UNION SELECT NO, NAME, STATECODE, NULL, NULL, DEPTCODE, HIREDATE FROM PROFESSOR ) LOGIN) u WHERE rn BETWEEN (#{pageNum}-1)*#{size}+1 AND #{pageNum}*#{size}
AND u.DEPTCODE = #{deptCode}
AND u.STATECODE = #{statecode}
AND u.no = #{no}
